import { Card, Image, Typography, } from 'antd';
import Highlighter from 'react-highlight-words';

import loadGIF from '../../images/loading.gif'

const { Text, Title } = Typography;

interface species {
    scientific_name: string;
    common_name: string;
    kingdom: string;
    phylum: string;
    _order: string;
    _class: string;
    family: string;
    image_link: string;
}

interface cardProps{
    data: species;
    searchVal: string;
    onClick: () => any;
}

function SpeciesCard(props: cardProps) {
    let searchArray = props.searchVal.split(' ')

    const cardProperties = [
        {
            text: 'Kingdom: ',
            highlightData: props.data.kingdom ? props.data.kingdom.toLocaleLowerCase() : null,
        },
        {
            text: 'Phylum: ',
            highlightData: props.data.phylum ? props.data.phylum.toLocaleLowerCase() : null,
        },
        {
            text: 'Class: ',
            hightlightData: props.data._class ? props.data._class.toLocaleLowerCase() : null,
        },
        {
            text: 'Order: ',
            highlightData: props.data._order ? props.data._order.toLocaleLowerCase() : null,
        },
        {
            text: 'Family: ',
            highlightData: props.data.family ? props.data.family.toLocaleLowerCase() : null,
        }
    ]

    let finishedProperties:Array<any> = []
    cardProperties.forEach((property) => {
        if(property.highlightData){
            finishedProperties.push(
                <>
                    <Text> {property.text}
                        <Highlighter
                            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
                            searchWords={searchArray}
                            autoEscape
                            textToHighlight={property.highlightData.toString()}
                        />
                    </Text><br/>
                </>
            )
        }
	})

    return(
        <Card 
            hoverable 
            onClick={props.onClick}
            style={{height: 'auto', padding: 0}}
            cover={
                <div style={{position: 'relative', width: '100%', height: '100%', paddingTop: '100%', }}>
                    <div style={{position: 'absolute', top: 0, left: 0, bottom:0, right:0}}>
                        <Image 
                            width='100%'
                            height='100%'
                            style={{objectFit:'cover'}}
                            alt={"Picture of "+props.data.scientific_name}
                            src={props.data.image_link}
                            placeholder={
                                <Image
                                preview={false}
                                src={loadGIF}
                                width='100%'
                                height='100%'
                                style={{objectFit:'cover'}}
                                alt="loading gif"
                                />
                            }
                            preview={false}
                            fallback="data:image/png;base64,"
                        />
                    </div>
                </div>
                
            }>
            <Title level={5} style={{padding: 0,}}>
                <Highlighter
                    highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
                    searchWords={searchArray}
                    autoEscape
                    textToHighlight={props.data.common_name ? props.data.common_name : props.data.scientific_name}
                />
            </Title>
            {finishedProperties}
        </Card>
    );
}

export default SpeciesCard;
